let pattern = /[a-zA-Z]/g;
function anagram(params1, params2) {
    if(params1.length === params2.length){
        let result1 = pattern.test(params1);
        let result2 = pattern.test(params2);
        if(result1 && result2){
            let word1 = params1.toLowerCase().split("").sort().join("");
            let word2 = params2.toLowerCase().split("").sort().join("");
            if(word1 === word2){
               return true;
            }else{
                return false;
            }
        }
    }
};